import React, { useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import Nav from "./Common/Nav";

export default function Education() {
    const handleLogout = () => {
        sessionStorage.removeItem('Auth Token');
        navigate('/login')
    }
    let navigate = useNavigate();
    useEffect(() => {
        let authToken = sessionStorage.getItem('Auth Token')
        console.log(authToken)
        if (authToken) {
            navigate('/education')
        }

        if (!authToken) {
            navigate('/login')
        }
    }, [])
    return (
        <div id="educationBoxContent">
            {/* the component for Education*/}
            <div className="ComponentTitle">
                <h1>Education</h1>
            </div>
            <div className="EducationCertificationContent">
                <div className="AddCertification">
                    <h1 style={{ fontSize: "1.5vw", textAlign: 'left' }}>Add certification</h1>
                    <div className="InputsColumns">
                        <div className="InputsEducation">
                            <Field
                                id="certificationBox"
                                type="text"
                                label="Certification"
                                required={true}
                                state={certification}
                                setState={setCertification}
                                pattern={/^(?=^.{0,70}$)([A-Z]?[a-z]+([ ]?[A-Z]?[a-z]+)*)$/}
                                validity={certificationValidity}
                                setValidity={setCertificationValidity}
                                infoMessage="Insert a certification."
                                errorMessage="You have to insert a certification!"
                                clear={clear}
                                setClear={setClear}
                            />

                            <Field
                                id="institutionBox"
                                type="text"
                                label="Institution"
                                required={true}
                                state={institution}
                                setState={setInstitution}
                                pattern={/^(?=^.{0,70}$)([A-Z]?[a-z]+([ ]?[A-Z]?[a-z]+)*)$/}
                                validity={institutionValidity}
                                setValidity={setInstitutionValidity}
                                infoMessage="Insert an institution."
                                errorMessage="You have exceeded the character limit!"
                                clear={clear}
                                setClear={setClear}
                            />

                            <Field
                                id="studyfieldBox"
                                type="text"
                                label="Field of study"
                                required={true}
                                state={field}
                                setState={setField}
                                pattern={/^(?=^.{0,70}$)([A-Z]?[a-z]+([ ]?[A-Z]?[a-z]+)*)$/}
                                validity={fieldValidity}
                                setValidity={setFieldValidity}
                                infoMessage="Insert a study field"
                                errorMessage="You have exceeded the character limit!"
                                clear={clear}
                                setClear={setClear}
                            />

                            <Field
                                id="cityBox"
                                type="text"
                                label="City"
                                required={true}
                                state={city}
                                setState={setCity}
                                pattern={/^(?=^.{0,40}$)([A-Z]?[a-z]+([ ]?[A-Z]?[a-z]+)*)$/}
                                validity={cityValidity}
                                setValidity={setCityValidity}
                                infoMessage="Insert a city."
                                errorMessage="You have exceeded the character limit!"
                                clear={clear}
                                setClear={setClear}
                            />
                            <div id="PeriodDate">
                                <Field
                                    id="startdateBox"
                                    className="EducationPeriod"
                                    label="Start Date:"
                                    type="date"
                                    required={true}
                                    state={startdate}
                                    setState={setStartDate}
                                    validity={startdateValidity}
                                    setValidity={setStartDateValidity}
                                />
                                {presentDate.display ? (
                                    // this classname is here because it has to match with the one in the ExperienceBox component
                                    // this class refers to the Field component for end date, so I couldn't create a duplicate in Experience.css
                                    <input style={{height:"27px"}} className="experienceInputsDate"
                                           id="enddateBox" value="Present" disabled />
                                ) : (
                                    <Field
                                        id="enddateBox"
                                        className="EducationPeriod"
                                        label="End Date:"
                                        type="date"
                                        required={true}
                                        state={enddate}
                                        setState={setEndDate}
                                        validity={enddateValidity}
                                        setValidity={setEndDateValidity}
                                    />
                                )}
                                <div>
                                    <button
                                        type="button"
                                        id="PresentButton"
                                        onClick={handleDateToPresent}
                                    >
                                        <FcCalendar />
                                    </button>
                                </div>
                            </div>
                        </div>
                        <div className="SummaryEducation">
                            <Field
                                id="summaryBox"
                                label="Summary"
                                field="textarea"
                                type="textarea"
                                required={true}
                                state={summary}
                                setState={setSummary}
                                validity={summaryValidity}
                                setValidity={setSummaryValidity}
                                infoMessage="Insert a summary."
                                errorMessage="You have exceeded the character limit!"
                                pattern={/^(?=^.{0,700}$)$/}
                                maxLength={700}
                                clear={clear}
                                setClear={setClear}
                            />
                        </div>
                    </div>
                    <div id="educationButtons" className="Buttons">
                        {!canceleducationEditable.display ? (
                            <div>
                                <button
                                    id="educationButtons"
                                    className="Clear"
                                    type="button"
                                    onClick={clearInputs}
                                >
                                    Clear
                                </button>
                            </div>
                        ) : (
                            <div>
                                <button
                                    id="educationButtons"
                                    type="button"
                                    className="Cancel"
                                    onClick={() =>
                                        cancelEdit(canceleducationEditable.indexToCancel)
                                    }
                                >
                                    Cancel
                                </button>
                            </div>
                        )}

                        {!educationInfoEditable.state ? (
                            <div>
                                <button
                                    id="educationButtons"
                                    className="Add"
                                    type="button"
                                    onClick={handleEducationAddInputs}
                                >
                                    Add
                                </button>
                            </div>
                        ) : (
                            <div>
                                <button
                                    id="educationButtons"
                                    className="Save"
                                    type="button"
                                    onClick={handleEducationEditInputs}
                                >
                                    Save
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            <div id="educationEditBox">
                <div id="educationEditBoxContent">
                    {userEducation.length === 0 ? (
                        <div className="emptyEducation">
                            <div><em style={{ color: "gray" }}>Education has not been added yet.</em></div>
                        </div>
                    ) : (
                        userEducation.map((userEducationInfo, index) => {
                            return (
                                <div
                                    className="educationBoxContentEdit"
                                    key={index}
                                    id={index + "div"}
                                >
                                    <div
                                        className="educationBoxContentEditIcons"
                                        id={index + "contentButtonsEd"}
                                    >
                                        <div>
                                            <button
                                                type="button"
                                                className="educationBoxIcons"
                                                onClick={() => handleEditContent(index)}
                                            >
                                                <FaPen />
                                            </button>
                                        </div>
                                        <div>
                                            <button
                                                type="button"
                                                className="educationBoxIcons"
                                                onClick={() => handleOpenModal(index)}
                                                id={index}
                                            >
                                                <FaTrashAlt />
                                            </button>
                                        </div>
                                    </div>
                                    <div
                                        className="educationBoxDescription"
                                        id={index + "contentEd"}
                                    >
                                        <div>
                      <span id={index + "_certification"}>
                        Certification: {userEducationInfo.certificationName}
                      </span>
                                            <br />
                                            <span id={index + "_institution"}>
                        Institution: {userEducationInfo.institutionName}
                      </span>
                                            <br />
                                            <span id={index + "_studyfield"}>
                        Field of study: {userEducationInfo.fieldOfStudy}
                      </span>
                                            <br />
                                            <span id={index + "_city"}>
                        City: {userEducationInfo.city}
                      </span>
                                            <br />
                                            <span>
                        Start Date:
                        <span id={index + "_startdate"}>
                          {userEducationInfo.period_from}
                        </span>
                        End Date:
                        <span id={index + "_enddate"}>
                          {userEducationInfo.period_to}
                        </span>
                      </span>
                                            <br />
                                        </div>
                                        <div>
                                            <button
                                                type="button"
                                                className="educationBoxIcons"
                                                onClick={() => handleDisplayDescription({ index })}
                                                id={index + "_displayEd"}
                                            >
                                                <FiArrowDown />
                                            </button>
                                        </div>
                                    </div>
                                    <div
                                        className="userEducationDescriptionContent"
                                        id={index + "descEd"}
                                    >
                    <textarea
                        value={userEducationInfo.summary}
                        disabled
                        style={{ width: "100%", resize: "none" }}
                    ></textarea>
                                    </div>
                                </div>
                            );
                        })
                    )}
                    <Modal
                        showDeleteModal={show}
                        setShowDeleteModal={setShow}
                        handleDelete={handleDeleteEducation}
                        title="education"
                        id={indexToEdit}
                    />
                </div>
            </div>
        </div>
    );
}
