import React, { useState } from 'react'
import { FaPen, FaTrashAlt } from "react-icons/fa";
import Modal from './Modal';

export default function ExperienceBoxEditArea({ userExperience, handleEditContent, handleDisplayDescription, handleDeleteExperience, showDeleteModal, setShowDeleteModal }) {

    const [indexToEdit, setIndexToEdit] = useState(null);
    function handleOpenModal(index) {
        setShowDeleteModal(true);
        setIndexToEdit(index);
    }
    return (
        <div id="experienceBoxContentEditBox">
            <div id="experienceBoxContentEditBoxContent">
                {userExperience.length === 0 ? <div className="emptyExperience"><em style={{ color: "gray" }}>Experience has not been added yet.</em></div>
                    :
                    userExperience.map((userExperienceInfo, index) => {
                        return <div className="experienceBoxContentEditBoxContentUserInfo" key={index} id={index + "div"}>
                            <div className="experienceBoxContentEditBoxContentUserInfoButtons" id={index + "contentButtons"}>
                                <div><button type="button" className="experienceBoxContentEditBoxContentUserInfoButtonsB" onClick={() => handleEditContent(index)}><FaPen fontSize="16px"/></button></div>
                                <div onClick={() => handleOpenModal(index)} id={index}><button type="button" className="experienceBoxContentEditBoxContentUserInfoButtonsB" ><FaTrashAlt fontSize="16px"/></button></div>
                            </div>
                            <div className="experienceBoxContentEditBoxContentUserInfoDescription" id={index + "content"}>
                                <div>
                                    <span id={index + "_role"}>Role: {userExperienceInfo.role}</span><br />
                                    <span id={index + "_employer"}>Employer: {userExperienceInfo.employer}</span><br />
                                    <span id={index + "_city"}>City: {userExperienceInfo.city}</span><br />
                                    <span>Period from: <span id={index + "_period_from"}>{userExperienceInfo.period_from.split('T')[0]}</span>to:<span id={index + "_period_to"}>{userExperienceInfo.period_to.split('T')[0]}</span></span><br />
                                </div>
                                <div>
                                    <button type="button" className="experienceBoxContentEditBoxContentUserInfoButtonsB" onClick={() => handleDisplayDescription({ index })} id={index + "_display"}>&#8595;</button>
                                </div>
                            </div>
                            <div className="userExperienceDescriptionContent" id={index + "desc"}><textarea value={userExperienceInfo.summary} disabled style={{ width: "100%", resize: "none" }}></textarea></div>
                        </div>
                    })
                }
                <Modal showDeleteModal={showDeleteModal} setShowDeleteModal={setShowDeleteModal} handleDelete={handleDeleteExperience} id={indexToEdit} title="experience" />
            </div>
        </div>
    )
}
