import React from 'react'
import { FcCalendar } from "react-icons/fc";
import Field from './Field';

export default function ExperienceBoxAddArea({ handleDateToPresent,
    handleExperienceAddInputs, handleExperienceClearInputs,
    handleExperienceEditInputs, experienceInfoEditable,
    dateIsToPresent, cancelExperienceInfoEditable,
    handleCancelEdit, roleValidity, setRoleValidity,
    employerValidity, setEmployerValidity,
    cityValidity, setCityValidity,
    summaryValidity, setSummaryValidity,
    clear, setClear,
    city, setCity, employer, setEmployer, role, setRole, summary, setSummary}) {

    return (
        <div id="experienceBoxContentAddBox">
            <div id="experienceBoxContentAddBoxTitleWrapper">
                <h1 style={{ fontSize: "1.5vw", textAlign: 'left' }}>Add experience</h1>
                <div id="experienceBoxContentAddBoxContentWrapper">
                    <div className="experienceBoxContentAddBoxInputs">
                        <Field id="experienceBoxRole"
                            label='Role'
                            field='input'
                            type='text'
                            required={true}
                            className="experienceInputs"
                            setState={setRole}
                            state={role}
                            validity={roleValidity}
                            setValidity={setRoleValidity}
                            infoMessage="Please insert your role."
                            errorMessage="Insert a valid role!"
                            pattern={/^(?=^.{0,30}$)([A-Z]?[a-z]+([ ]?[A-Z]?[a-z]+)*)$/}
                            clear={clear}
                            setClear={setClear}
                        />
                        <Field id="experienceBoxEmployer"
                            label='Employer'
                            field='input'
                            type='text'
                            required={true}
                            className="experienceInputs"
                            setState={setEmployer}
                            state={employer}
                            validity={employerValidity}
                            setValidity={setEmployerValidity}
                            infoMessage="Please insert your employer."
                            errorMessage="Insert a valid employer!"
                            pattern={/^(?=^.{0,70}$)([A-Z]?[a-z]+([ ]?[A-Z]?[a-z]+)*)$/}
                            clear={clear}
                            setClear={setClear}
                        />
                        <Field id="experienceBoxCity"
                            label='City'
                            field='input'
                            type='text'
                            required={true}
                            className="experienceInputs"
                            setState={setCity}
                            state={city}
                            validity={cityValidity}
                            setValidity={setCityValidity}
                            infoMessage="Please insert your city."
                            errorMessage="Insert a valid city!"
                            pattern={/^(?=^.{0,30}$)([A-Z]?[a-z]+([ ]?[A-Z]?[a-z]+)*)$/}
                            clear={clear}
                            setClear={setClear}
                        />
                        <div id="experienceBoxContentAddBoxInputsDate">
                            <Field id="experienceBoxPeriodFrom"
                                label='Start Date:'
                                field='input'
                                type='date'
                                required={true}
                                className="experienceInputsDate"
                            />
                            {dateIsToPresent.display ?
                                <input className="experienceInputsDate" id="experienceBoxPeriodTo" value="Present" disabled style={{ height: "27px", borderRadius: "5px", border: "1px solid gray"}} />
                                :
                                <Field id="experienceBoxPeriodTo"
                                    label='End Date:'
                                    field='input'
                                    type='date'
                                    required={true}
                                    className="experienceInputsDate"
                                />
                            }
                            <div>
                                <button type="button" id="toPresentButton" onClick={handleDateToPresent}><FcCalendar fontSize="16px"/></button>
                            </div>
                        </div>
                    </div>
                    <div className="experienceBoxContentAddBoxInputs">
                        <Field id="experienceBoxTextarea"
                            label='Summary'
                            field='textarea'
                            type='textarea'
                            required={true}
                            className="experienceTextarea"
                            setState={setSummary}
                            state={summary}
                            validity={summaryValidity}
                            setValidity={setSummaryValidity}
                            infoMessage="Please insert your summary."
                            errorMessage="Summary too long!"
                            pattern={/^(?=^.{0,700}$)$/}
                            maxLength={700}
                            clear={clear}
                            setClear={setClear}
                        />
                    </div>
                </div>
                <div id="experienceBoxContentAddBoxButtons">
                    {(!cancelExperienceInfoEditable.display) ?
                        <div><button id="experienceBoxContentAddBoxButtonsClear" type="button" onClick={handleExperienceClearInputs}>Clear</button></div>
                        :
                        <div><button id="experienceBoxContentAddBoxButtonsClear" type="button" onClick={()=>handleCancelEdit(cancelExperienceInfoEditable.indexToCancel)}>Cancel</button></div>
                    }
                    {(!experienceInfoEditable.state) ?
                        <div><button id="experienceBoxContentAddBoxButtonsAdd" type="button" onClick={handleExperienceAddInputs}>Add</button></div>
                        :
                        <div><button id="experienceBoxContentAddBoxButtonsAdd" type="button" onClick={handleExperienceEditInputs}>Save</button></div>
                    }
                </div>
            </div>
        </div>
    )
}
